#include <stdint.h>
//! Lesson 37 Periphereal clock configuration

// APB2 ADC ENABLE
#define RCC_BASE_ADRR		0x40023800UL
#define RCC_APB2_ENR_OFFSET 0x44UL
#define RCC_APB2_ENR_ADDR   (RCC_BASE_ADRR + RCC_APB2_ENR_OFFSET)

// ADC
#define ADC_BASE_ADDR		0x40012000UL	// Memory map
#define ADC_CR1_REG_OFFSET	0x04UL			// ADC registers
#define ADC_CR1_REG_ADDR	(ADC_BASE_ADDR + ADC_CR1_REG_OFFSET)


#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
    uint32_t *pRccApb2Enr = (uint32_t*) RCC_APB2_ENR_ADDR;
	uint32_t *pAdcCr1Reg = (uint32_t*) ADC_CR1_REG_ADDR;

	//! Enable the peripheral clock for ADC1
	*pRccApb2Enr |= (1 << 8); //! set the 8bit to 1
	//! Enable the peripheral clock for ADC1 CR1
	*pAdcCr1Reg |= (1 << 8); //! set the 8bit to 1
	for(;;);
}
